import React from 'react';
import './CardGrid.css';

const CardGrid = ({ cards, loading }) => {
  if (loading) {
    return (
      <div className="card-grid-container">
        <div className="loading">Loading cards...</div>
      </div>
    );
  }

  if (!cards || cards.length === 0) {
    return (
      <div className="card-grid-container">
        <div className="no-cards">
          <p>No cards to display. Enter a decklist above to see the preview.</p>
        </div>
      </div>
    );
  }

  // Show first 9 cards in the preview grid
  const previewCards = cards.slice(0, 9);
  const remainingCards = cards.length - 9;

  return (
    <div className="card-grid-container mt-6">
      <div className="card-grid">
        {previewCards.map((card, index) => (
          <div key={index} className="card-item">
            <img
              src={card.imageUrl}
              alt={card.name}
              className="card-image"
              onError={(e) => {
                e.target.style.display = 'none';
                e.target.nextSibling.style.display = 'flex';
              }}
            />
            <div className="card-placeholder">
              <span>{card.name}</span>
            </div>
          </div>
        ))}
      </div>
      
      {remainingCards > 0 && (
        <div className="text-center my-4 p-2 bg-blue-50 rounded text-blue-700 font-semibold">
          + {remainingCards} more cards will be included in the PDF
        </div>
      )}
      
      <div className="text-center mt-4 p-4 bg-white rounded shadow">
        <p className="font-semibold text-gray-700">Total cards: {cards.length}</p>
        <p className="font-semibold text-gray-700">Pages needed: {Math.ceil(cards.length / 9)}</p>
      </div>
    </div>
  );
};

export default CardGrid;
