import React, { useState } from 'react';
import './CardGrid.css';

const CardGrid = ({ cards, loading, onSelectPrinting }) => {
  const [imgErrors, setImgErrors] = useState([]);

  if (loading) {
    return (
      <div className="card-grid-container">
        <div className="loading">Loading cards...</div>
      </div>
    );
  }

  if (!cards || cards.length === 0) {
    return (
      <div className="card-grid-container">
        <div className="no-cards">
          <p>No cards to display. Enter a decklist above to see the preview.</p>
        </div>
      </div>
    );
  }

  // Show first 9 cards in the preview grid
  const previewCards = cards.slice(0, 9);
  const remainingCards = cards.length - 9;

  const handleImgError = (index) => {
    setImgErrors((prev) => {
      const updated = [...prev];
      updated[index] = true;
      return updated;
    });
  };

  return (
    <div className="card-grid-container mt-6">
      <div className="card-grid">
        {previewCards.map((card, index) => (
          <div key={index} className="card-item-wrapper">
            <div className="card-item">
              {!imgErrors[index] ? (
                <img
                  src={card.imageUrl}
                  alt={card.name}
                  className="card-image"
                  onError={() => handleImgError(index)}
                  onLoad={() => {
                    console.log(`Successfully loaded image for ${card.name}`);
                  }}
                />
              ) : (
                <div className="card-placeholder">
                  <span>{card.name}</span>
                </div>
              )}
            </div>
            {/* Dropdown for selecting printing/version - now outside the clipped card-item */}
            {card.printings && card.printings.length > 0 && (
              <select
                value={card.selectedPrintingId || card.printings[0].id}
                onChange={e => onSelectPrinting(index, e.target.value)}
                className="card-version-dropdown"
              >
                {card.printings.map((printing) => (
                  <option key={printing.id} value={printing.id}>
                    {printing.set_name} ({printing.collector_number})
                  </option>
                ))}
              </select>
            )}
          </div>
        ))}
      </div>
      
      {remainingCards > 0 && (
        <div className="text-center my-4 p-2 bg-blue-50 rounded text-blue-700 font-semibold">
          + {remainingCards} more cards will be included in the PDF
        </div>
      )}
      
      <div className="text-center mt-4 p-4 bg-white rounded shadow">
        <p className="font-semibold text-gray-700">Total cards: {cards.length}</p>
        <p className="font-semibold text-gray-700">Pages needed: {Math.ceil(cards.length / 9)}</p>
      </div>
    </div>
  );
};

export default CardGrid;
