import React, { useState } from 'react';
import './DeckInput.css';

const DeckInput = ({ onDeckSubmit, loading }) => {
  const [decklist, setDecklist] = useState('');
  const [errors, setErrors] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors([]);
    
    if (!decklist.trim()) {
      setErrors(['Please enter a decklist']);
      return;
    }
    
    onDeckSubmit(decklist);
  };

  const handleClear = () => {
    setDecklist('');
    setErrors([]);
  };

  const handleExample = () => {
    const exampleDeck = `4x Lightning Bolt (M11) 146
4x Counterspell (M10) 58
4x Brainstorm (C21) 85
4x Ponder (M12) 74
4x Preordain (M11) 66
4x Delver of Secrets (ISD) 51
4x Snapcaster Mage (ISD) 74
4x Young Pyromancer (M14) 172
4x Monastery Swiftspear (KTK) 134
4x Eidolon of the Great Revel (BNG) 100
4x Sulfuric Vortex (SCG) 95
4x Price of Progress (EXO) 127
4x Fireblast (VIS) 123
4x Chain Lightning (LEG) 95
4x Lava Spike (CHK) 175
4x Rift Bolt (TSP) 165
4x Searing Blaze (ROE) 136
4x Smash to Smithereens (M10) 150
4x Pyroblast (ICE) 108
4x Red Elemental Blast (3ED) 127`;
    
    setDecklist(exampleDeck);
  };

  const handleSimpleExample = () => {
    const simpleDeck = `4x Lightning Bolt
4x Counterspell
4x Brainstorm
4x Ponder
4x Preordain
4x Delver of Secrets
4x Snapcaster Mage
4x Young Pyromancer
4x Monastery Swiftspear
4x Eidolon of the Great Revel`;
    
    setDecklist(simpleDeck);
  };

  return (
    <div className="bg-white rounded-xl shadow-lg p-6 flex flex-col gap-4 w-full max-w-md">
      <form onSubmit={handleSubmit} className="flex flex-col gap-4">
        <div className="mb-2">
          <h2 className="text-xl font-bold text-gray-800 mb-1">Enter Your Decklist</h2>
          <p className="text-gray-600 text-sm mb-1">Paste your Magic: The Gathering decklist below (one card per line)</p>
          <p className="italic text-xs text-gray-400">Format: "1x Lightning Bolt (M11) 146" or "1 Lightning Bolt"</p>
        </div>
        <div>
          <textarea
            value={decklist}
            onChange={(e) => setDecklist(e.target.value)}
            placeholder={"4x Lightning Bolt (M11) 146\n4x Counterspell (M10) 58\n4x Brainstorm (C21) 85\n..."}
            disabled={loading}
            rows={12}
            className="decklist-textarea font-mono w-full border-2 border-gray-200 rounded-lg p-3 text-sm focus:border-blue-400 focus:ring-2 focus:ring-blue-100 min-h-[200px] transition disabled:bg-gray-100 disabled:text-gray-400 disabled:cursor-not-allowed"
          />
        </div>
        {errors.length > 0 && (
          <div className="bg-red-100 border border-red-300 text-red-700 rounded p-2 text-xs">
            {errors.map((error, index) => (
              <p key={index}>{error}</p>
            ))}
          </div>
        )}
        <div className="flex gap-2 justify-end">
          <button
            type="submit"
            className="btn btn-success bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded shadow disabled:opacity-60"
            disabled={loading || !decklist.trim()}
          >
            {loading ? (<span className="animate-spin mr-2">🔄</span>) : null}
            {loading ? ' Loading Cards...' : 'Load Cards'}
          </button>
          <button
            type="button"
            className="btn btn-secondary bg-gray-400 hover:bg-gray-500 text-white px-4 py-2 rounded shadow"
            onClick={handleClear}
            disabled={loading}
          >
            Clear
          </button>
        </div>
      </form>
    </div>
  );
};

export default DeckInput;
