import React, { useState } from 'react';
import './DeckInput.css';

const DeckInput = ({ onDeckSubmit, loading }) => {
  const [decklist, setDecklist] = useState('');
  const [errors, setErrors] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors([]);
    
    if (!decklist.trim()) {
      setErrors(['Please enter a decklist']);
      return;
    }
    
    onDeckSubmit(decklist);
  };

  const handleClear = () => {
    setDecklist('');
    setErrors([]);
  };

  const handleExample = () => {
    const exampleDeck = `4x Lightning Bolt (2XM) 123
4x Counterspell (2XM) 124
4x Brainstorm (2XM) 125
4x Ponder (2XM) 126
4x Preordain (2XM) 127
4x Delver of Secrets (2XM) 128
4x Snapcaster Mage (2XM) 129
4x Young Pyromancer (2XM) 130
4x Monastery Swiftspear (2XM) 131
4x Eidolon of the Great Revel (2XM) 132
4x Sulfuric Vortex (2XM) 133
4x Price of Progress (2XM) 134
4x Fireblast (2XM) 135
4x Chain Lightning (2XM) 136
4x Lava Spike (2XM) 137
4x Rift Bolt (2XM) 138
4x Searing Blaze (2XM) 139
4x Smash to Smithereens (2XM) 140
4x Pyroblast (2XM) 141
4x Red Elemental Blast (2XM) 142`;
    
    setDecklist(exampleDeck);
  };

  return (
    <div className="deck-input-container">
      <form onSubmit={handleSubmit}>
        <div className="input-header">
          <h2>Enter Your Decklist</h2>
          <p>Paste your Magic: The Gathering decklist below (one card per line)</p>
          <p className="format-example">
            Format: "1x Lightning Bolt (2XM) 123" or "1 Lightning Bolt"
          </p>
        </div>
        
        <div className="textarea-container">
          <textarea
            value={decklist}
            onChange={(e) => setDecklist(e.target.value)}
            placeholder="4x Lightning Bolt (2XM) 123&#10;4x Counterspell (2XM) 124&#10;4x Brainstorm (2XM) 125&#10;..."
            disabled={loading}
            rows={12}
            className="decklist-textarea"
          />
        </div>
        
        {errors.length > 0 && (
          <div className="error">
            {errors.map((error, index) => (
              <p key={index}>{error}</p>
            ))}
          </div>
        )}
        
        <div className="button-group">
          <button
            type="submit"
            className="btn btn-success"
            disabled={loading || !decklist.trim()}
          >
            {loading ? 'Loading Cards...' : 'Load Cards'}
          </button>
          
          <button
            type="button"
            className="btn btn-secondary"
            onClick={handleClear}
            disabled={loading}
          >
            Clear
          </button>
          
          <button
            type="button"
            className="btn"
            onClick={handleExample}
            disabled={loading}
          >
            Load Example
          </button>
        </div>
      </form>
    </div>
  );
};

export default DeckInput;
