import React, { useState } from 'react';
import './DeckInput.css';

const DeckInput = ({ onDeckSubmit, loading, invalidLines = [] }) => {
  const [decklist, setDecklist] = useState('');
  const [errors, setErrors] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrors([]);
    
    if (!decklist.trim()) {
      setErrors(['Please enter a decklist']);
      return;
    }
    
    onDeckSubmit(decklist);
  };

  const handleClear = () => {
    setDecklist('');
    setErrors([]);
  };

  const handleExample = () => {
    const exampleDeck = `4x Lightning Bolt (M11) 146
4x Counterspell (M10) 58
4x Brainstorm (C21) 85
4x Ponder (M12) 74
4x Preordain (M11) 66
4x Delver of Secrets (ISD) 51
4x Snapcaster Mage (ISD) 74
4x Young Pyromancer (M14) 172
4x Monastery Swiftspear (KTK) 134
4x Eidolon of the Great Revel (BNG) 100
4x Sulfuric Vortex (SCG) 95
4x Price of Progress (EXO) 127
4x Fireblast (VIS) 123
4x Chain Lightning (LEG) 95
4x Lava Spike (CHK) 175
4x Rift Bolt (TSP) 165
4x Searing Blaze (ROE) 136
4x Smash to Smithereens (M10) 150
4x Pyroblast (ICE) 108
4x Red Elemental Blast (3ED) 127`;
    
    setDecklist(exampleDeck);
  };

  const handleSimpleExample = () => {
    const simpleDeck = `4x Lightning Bolt
4x Counterspell
4x Brainstorm
4x Ponder
4x Preordain
4x Delver of Secrets
4x Snapcaster Mage
4x Young Pyromancer
4x Monastery Swiftspear
4x Eidolon of the Great Revel`;
    
    setDecklist(simpleDeck);
  };

  // Helper to highlight invalid lines
  const getTextareaValue = () => {
    if (invalidLines.length === 0) return decklist;
    // Split lines and wrap invalid ones (for display only, not editable)
    return decklist
      .split('\n')
      .map((line, idx) =>
        invalidLines.includes(idx)
          ? `‚ùå ${line}`
          : line
      )
      .join('\n');
  };

  return (
    <div className="bg-white rounded-xl shadow p-6 w-full flex flex-col gap-6">
      <form onSubmit={handleSubmit} className="flex flex-col gap-6">
        <div className="mb-2">
          <h2 className="text-2xl font-bold text-gray-800 mb-1">Enter Your Decklist</h2>
          <p className="text-gray-600 text-base mb-1">Paste your Magic: The Gathering decklist below (one card per line)</p>
          <p className="italic text-sm text-gray-400">Format: "1x Lightning Bolt (M11) 146" or "1 Lightning Bolt"</p>
        </div>
        <div>
          <textarea
            value={getTextareaValue()}
            onChange={(e) => setDecklist(e.target.value)}
            placeholder={"4x Lightning Bolt (M11) 146\n4x Counterspell (M10) 58\n4x Brainstorm (C21) 85\n..."}
            disabled={loading}
            rows={12}
            className={`decklist-textarea font-mono w-full border-2 rounded-lg p-4 text-base min-h-[200px] transition ${invalidLines.length ? 'border-red-400 bg-red-50' : 'border-gray-200'} focus:border-blue-400 focus:ring-2 focus:ring-blue-100 disabled:bg-gray-100 disabled:text-gray-400 disabled:cursor-not-allowed`}
          />
        </div>
        {errors.length > 0 && (
          <div className="bg-red-100 border border-red-300 text-red-700 rounded p-3 text-base">
            {errors.map((error, index) => (
              <p key={index}>{error}</p>
            ))}
          </div>
        )}
        {invalidLines.length > 0 && (
          <div className="text-base text-red-600 mt-1">Some cards could not be found. Lines marked with ‚ùå are invalid.</div>
        )}
        <div className="flex gap-4 justify-end">
          <button
            type="submit"
            className="btn btn-success bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded-full shadow text-lg font-bold disabled:opacity-60"
            disabled={loading || !decklist.trim()}
          >
            {loading ? (<span className="animate-spin mr-2">üîÑ</span>) : null}
            {loading ? ' Loading Cards...' : 'Load Cards'}
          </button>
          <button
            type="button"
            className="btn btn-secondary bg-gray-400 hover:bg-gray-500 text-white px-6 py-3 rounded-full shadow text-lg font-bold"
            onClick={handleClear}
            disabled={loading}
          >
            Clear
          </button>
        </div>
      </form>
    </div>
  );
};

export default DeckInput;
