import React, { useState } from 'react';
import DeckInput from './components/DeckInput';
import CardGrid from './components/CardGrid';
import { parseDecklist, fetchMultipleCards, fetchAllPrintings } from './utils/scryfall';
import { generatePDF, downloadPDF } from './utils/pdfGenerator';
import './App.css';

function App() {
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);
  const [generatingPDF, setGeneratingPDF] = useState(false);
  const [errors, setErrors] = useState([]);
  const [success, setSuccess] = useState('');
  const [selectedPaperSize, setSelectedPaperSize] = useState('letter');
  const [scale, setScale] = useState(100);
  const [cropMarks, setCropMarks] = useState(true);
  const [blackCorners, setBlackCorners] = useState(false);
  const [cutLines, setCutLines] = useState(false);
  const [skipBasicLands, setSkipBasicLands] = useState(false);

  // Track selected printing for each card (by index)
  const [selectedPrintings, setSelectedPrintings] = useState({});

  // Paper size options
  const paperSizes = [
    { value: 'letter', label: 'Letter (8.5x11.0 in)', width: 8.5, height: 11.0 },
    { value: 'legal', label: 'Legal (8.5x14.0 in)', width: 8.5, height: 14.0 },
    { value: 'a4', label: 'A4 (8.27x11.69 in)', width: 8.27, height: 11.69 },
    { value: 'a3', label: 'A3 (11.69x16.54 in)', width: 11.69, height: 16.54 },
    { value: 'tabloid', label: 'Tabloid (11x17 in)', width: 11, height: 17 },
  ];

  const handleDeckSubmit = async (decklistText) => {
    setLoading(true);
    setErrors([]);
    setSuccess('');
    setCards([]);
    setSelectedPrintings({});

    try {
      // Parse the decklist
      const parsedCards = parseDecklist(decklistText);
      if (parsedCards.length === 0) {
        setErrors(['No valid cards found in the decklist. Please check the format.']);
        setLoading(false);
        return;
      }

      // Fetch card data from Scryfall
      const { results, errors: fetchErrors } = await fetchMultipleCards(parsedCards);
      if (results.length === 0) {
        setErrors(['No cards could be found. Please check the card names and try again.']);
        setLoading(false);
        return;
      }

      // For each card, fetch all printings and set up the card object
      const cardsWithPrintings = await Promise.all(results.map(async (card, idx) => {
        let printings = [];
        try {
          if (card.prints_search_uri) {
            printings = await fetchAllPrintings(card.prints_search_uri);
          }
        } catch (e) {
          printings = [];
        }
        // Default to the originally loaded card
        const selectedPrinting = printings.find(p => p.set_name === card.set && p.collector_number === card.collectorNumber) || printings[0];
        const cardWithPrintings = {
          ...card,
          printings: printings.map(p => ({
            id: p.id,
            set_name: p.set_name,
            collector_number: p.collector_number,
            released_at: p.released_at,
            imageUrl: p.image_uris?.png || p.card_faces?.[0]?.image_uris?.png,
          })),
          selectedPrintingId: selectedPrinting ? selectedPrinting.id : null,
        };
        console.log('Card with printings:', cardWithPrintings.name, 'printings:', cardWithPrintings.printings.length, cardWithPrintings.printings[0]);
        return cardWithPrintings;
      }));

      // Proxy the image URLs for the selected printing
      const proxiedCards = cardsWithPrintings.map(card => {
        const selected = card.printings.find(p => p.id === card.selectedPrintingId) || card.printings[0];
        return {
          ...card,
          imageUrl: selected && selected.imageUrl ? `http://localhost:4000/proxy?url=${encodeURIComponent(selected.imageUrl)}` : card.imageUrl,
        };
      });

      setCards(proxiedCards);

      if (fetchErrors.length > 0) {
        setErrors(fetchErrors);
      } else {
        setSuccess(`Successfully loaded ${results.length} cards!`);
      }
    } catch (error) {
      setErrors([`Error loading cards: ${error.message}`]);
    } finally {
      setLoading(false);
    }
  };

  // Handler for when a user selects a different printing
  const handleSelectPrinting = (cardIdx, printingId) => {
    setCards(prevCards => prevCards.map((card, idx) => {
      if (idx !== cardIdx) return card;
      const selected = card.printings.find(p => p.id === printingId) || card.printings[0];
      return {
        ...card,
        selectedPrintingId: selected.id,
        imageUrl: selected && selected.imageUrl
          ? `http://localhost:4000/proxy?url=${encodeURIComponent(selected.imageUrl)}`
          : card.imageUrl,
      };
    }));
  };

  const handleGeneratePDF = async () => {
    if (cards.length === 0) {
      setErrors(['No cards to generate PDF for. Please load a decklist first.']);
      return;
    }
    setGeneratingPDF(true);
    setErrors([]);
    setSuccess('');
    try {
      // Find the selected paper size object
      const paper = paperSizes.find(p => p.value === selectedPaperSize) || paperSizes[0];
      // Pass paper size and scale to generatePDF
      const pdfBytes = await generatePDF(cards, paper, scale);
      downloadPDF(pdfBytes, 'mtg-deck.pdf');
      setSuccess('PDF generated and downloaded successfully!');
    } catch (error) {
      setErrors([`Error generating PDF: ${error.message}`]);
    } finally {
      setGeneratingPDF(false);
    }
  };

  return (
    <div className="App">
      <div className="container">
        <header className="app-header">
          <h1>MTG PDF Printer</h1>
          <p>Generate print-ready PDFs from Magic: The Gathering decklists</p>
        </header>

        <div className="card">
          <DeckInput
            onDeckSubmit={handleDeckSubmit}
            loading={loading}
            onGeneratePDF={handleGeneratePDF}
            generatingPDF={generatingPDF}
            canGeneratePDF={cards.length > 0}
          />
          {/* Crop Marks, Cut Lines, and Skip Basic Lands Toggles */}
          <div className="toggle-section" style={{ marginTop: 16, marginBottom: 8, textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 32 }}>
            <label style={{ display: 'flex', alignItems: 'center', gap: 8, cursor: 'pointer' }}>
              <span style={{ fontWeight: 600 }}>Crop marks</span>
              <div
                onClick={() => setCropMarks(v => !v)}
                style={{
                  width: 40,
                  height: 22,
                  borderRadius: 22,
                  background: cropMarks ? '#8bc34a' : '#bbb',
                  position: 'relative',
                  transition: 'background 0.2s',
                  display: 'inline-block',
                  verticalAlign: 'middle',
                  cursor: 'pointer',
                }}
                tabIndex={0}
                role="switch"
                aria-checked={cropMarks}
              >
                <div
                  style={{
                    width: 18,
                    height: 18,
                    borderRadius: '50%',
                    background: '#fff',
                    position: 'absolute',
                    top: 2,
                    left: cropMarks ? 20 : 2,
                    transition: 'left 0.2s',
                    boxShadow: '0 1px 4px rgba(0,0,0,0.15)'
                  }}
                />
              </div>
            </label>
            <label style={{ display: 'flex', alignItems: 'center', gap: 8, cursor: 'pointer' }}>
              <span style={{ fontWeight: 600 }}>Cut lines</span>
              <div
                onClick={() => setCutLines(v => !v)}
                style={{
                  width: 40,
                  height: 22,
                  borderRadius: 22,
                  background: cutLines ? '#8bc34a' : '#bbb',
                  position: 'relative',
                  transition: 'background 0.2s',
                  display: 'inline-block',
                  verticalAlign: 'middle',
                  cursor: 'pointer',
                }}
                tabIndex={0}
                role="switch"
                aria-checked={cutLines}
              >
                <div
                  style={{
                    width: 18,
                    height: 18,
                    borderRadius: '50%',
                    background: '#fff',
                    position: 'absolute',
                    top: 2,
                    left: cutLines ? 20 : 2,
                    transition: 'left 0.2s',
                    boxShadow: '0 1px 4px rgba(0,0,0,0.15)'
                  }}
                />
              </div>
            </label>
            <label style={{ display: 'flex', alignItems: 'center', gap: 8, cursor: 'pointer' }}>
              <span style={{ fontWeight: 600 }}>Skip basic lands</span>
              <div
                onClick={() => setSkipBasicLands(v => !v)}
                style={{
                  width: 40,
                  height: 22,
                  borderRadius: 22,
                  background: skipBasicLands ? '#8bc34a' : '#bbb',
                  position: 'relative',
                  transition: 'background 0.2s',
                  display: 'inline-block',
                  verticalAlign: 'middle',
                  cursor: 'pointer',
                }}
                tabIndex={0}
                role="switch"
                aria-checked={skipBasicLands}
              >
                <div
                  style={{
                    width: 18,
                    height: 18,
                    borderRadius: '50%',
                    background: '#fff',
                    position: 'absolute',
                    top: 2,
                    left: skipBasicLands ? 20 : 2,
                    transition: 'left 0.2s',
                    boxShadow: '0 1px 4px rgba(0,0,0,0.15)'
                  }}
                />
              </div>
            </label>
          </div>
          {/* Paper Size Dropdown */}
          <div className="paper-size-section" style={{ marginTop: 16, marginBottom: 16, textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 16 }}>
            <label htmlFor="paper-size-select" style={{ fontWeight: 600, marginRight: 8 }}>Paper Size</label>
            <select
              id="paper-size-select"
              value={selectedPaperSize}
              onChange={e => setSelectedPaperSize(e.target.value)}
              style={{ padding: '4px 12px', borderRadius: 6, border: '1px solid #ccc', fontSize: 16 }}
            >
              {paperSizes.map(size => (
                <option key={size.value} value={size.value}>{size.label}</option>
              ))}
            </select>
            <label htmlFor="scale-input" style={{ fontWeight: 600, marginLeft: 24, marginRight: 8 }}>Scale</label>
            <input
              id="scale-input"
              type="number"
              min={90}
              max={110}
              step={1}
              value={scale}
              onChange={e => setScale(Number(e.target.value))}
              style={{ width: 70, padding: '4px 8px', borderRadius: 6, border: '1px solid #ccc', fontSize: 16 }}
            />
            <span style={{ marginLeft: 4 }}>%</span>
          </div>
        </div>

        {errors.length > 0 && (
          <div className="card">
            <div className="error">
              <h3>Errors:</h3>
              {errors.map((error, index) => (
                <p key={index}>{error}</p>
              ))}
            </div>
          </div>
        )}

        {success && (
          <div className="card">
            <div className="success">
              <p>{success}</p>
            </div>
          </div>
        )}

        {cards.length > 0 && (
          <div className="card">
            <div className="preview-section">
              <h2>Card Preview (3x3 Grid)</h2>
              <CardGrid cards={cards} loading={loading} onSelectPrinting={handleSelectPrinting} />
              <div className="pdf-actions">
                <button
                  className="btn btn-success"
                  onClick={handleGeneratePDF}
                  disabled={generatingPDF || cards.length === 0}
                >
                  {generatingPDF ? 'Generating PDF...' : 'Generate PDF'}
                </button>
                <div className="pdf-info">
                  <p>• Print-ready PDF with 9 cards per page</p>
                  <p>• Standard card size (2.5" x 3.5")</p>
                  <p>• Minimal margins for optimal printing</p>
                  <p>• No watermarks or branding</p>
                </div>
              </div>
            </div>
          </div>
        )}

        <footer className="app-footer">
          <div className="footer-content">
            <p>&copy; 2024 MTG PDF Printer. Made with ❤️ for Magic players.</p>
            <div className="coffee-button">
              <a href="https://www.buymeacoffee.com/gOZTM9e" target="_blank" rel="noopener noreferrer">
                <img 
                  src="https://img.buymeacoffee.com/button-api/?text=Buy me a coffee&emoji=&slug=gOZTM9e&button_colour=FFDD00&font_colour=000000&font_family=Poppins&outline_colour=000000&coffee_colour=ffffff" 
                  alt="Buy me a coffee"
                />
              </a>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
}

export default App;
