import React, { useState } from 'react';
import { useTheme } from '../ThemeContext';

const paperSizes = [
  { label: 'Letter (8.5x11.0 in)', value: 'letter' },
  { label: 'A4 (210x297 mm)', value: 'a4' },
];
const scales = [
  { label: '100%', value: '100' },
  { label: '95%', value: '95' },
  { label: '90%', value: '90' },
];

const ToggleSwitch = ({ checked, onChange, label, id }) => (
  <label className="flex items-center space-x-2 cursor-pointer select-none">
    <span className="text-sm text-gray-700 dark:text-gray-200 font-medium">{label}</span>
    <button
      type="button"
      role="switch"
      aria-checked={checked}
      id={id}
      tabIndex={0}
      onClick={() => onChange(!checked)}
      onKeyDown={e => (e.key === 'Enter' || e.key === ' ') && onChange(!checked)}
      className={`relative inline-flex h-6 w-11 items-center rounded-full transition-colors focus:outline-none focus:ring-2 focus:ring-blue-400 ${checked ? 'bg-blue-600' : 'bg-gray-300 dark:bg-gray-600'}`}
    >
      <span
        className={`inline-block h-5 w-5 transform rounded-full bg-white shadow transition-transform ${checked ? 'translate-x-5' : 'translate-x-1'}`}
      />
    </button>
  </label>
);

const PrintOptions = ({ onPrint, printing }) => {
  const [cropMarks, setCropMarks] = useState(true);
  const [cutLines, setCutLines] = useState(false);
  const [blackCorners, setBlackCorners] = useState(true);
  const [skipBasicLands, setSkipBasicLands] = useState(false);
  const [printChecklist, setPrintChecklist] = useState(false);
  const [playtestWatermark, setPlaytestWatermark] = useState(false);
  const [paperSize, setPaperSize] = useState('letter');
  const [scale, setScale] = useState('100');
  const { theme, setTheme } = useTheme();

  return (
    <div className="flex flex-row items-center space-x-4 w-full flex-wrap md:flex-nowrap">
      <ToggleSwitch checked={cropMarks} onChange={setCropMarks} label="Crop marks" id="crop-marks" />
      <ToggleSwitch checked={cutLines} onChange={setCutLines} label="Cut lines" id="cut-lines" />
      <ToggleSwitch checked={blackCorners} onChange={setBlackCorners} label="Black corners" id="black-corners" />
      <ToggleSwitch checked={skipBasicLands} onChange={setSkipBasicLands} label="Skip basic lands" id="skip-basic-lands" />
      <ToggleSwitch checked={printChecklist} onChange={setPrintChecklist} label="Print checklist" id="print-checklist" />
      <ToggleSwitch checked={playtestWatermark} onChange={setPlaytestWatermark} label="Playtest watermark" id="playtest-watermark" />
      <div className="flex flex-col md:flex-row md:items-center space-y-2 md:space-y-0 md:space-x-2 ml-auto">
        <div>
          <label className="text-xs text-gray-600 dark:text-gray-300 mb-1 mr-1">Paper Size</label>
          <select
            className="border rounded px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={paperSize}
            onChange={e => setPaperSize(e.target.value)}
          >
            {paperSizes.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
        <div>
          <label className="text-xs text-gray-600 dark:text-gray-300 mb-1 mr-1">Scale</label>
          <select
            className="border rounded px-2 py-1 text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={scale}
            onChange={e => setScale(e.target.value)}
          >
            {scales.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
        <button
          className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold px-5 py-2 rounded shadow focus:outline-none focus:ring-2 focus:ring-blue-400 transition disabled:opacity-60 ml-2"
          onClick={onPrint}
          disabled={printing}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 9V2h12v7M6 18H4a2 2 0 01-2-2v-5a2 2 0 012-2h16a2 2 0 012 2v5a2 2 0 01-2 2h-2m-6 0v4m0 0h4m-4 0H8" /></svg>
          Print
        </button>
      </div>
      <div className="ml-4 flex-shrink-0">
        <div className="flex gap-2 items-center">
          <span className="text-xs text-gray-500 dark:text-gray-300">Theme:</span>
          <button
            className={`px-2 py-1 rounded text-xs font-medium focus:outline-none focus:ring-2 focus:ring-blue-400 ${theme === 'light' ? 'bg-blue-600 text-white' : 'bg-gray-100 dark:bg-gray-700 dark:text-gray-200'}`}
            onClick={() => setTheme('light')}
            aria-pressed={theme === 'light'}
          >Light</button>
          <button
            className={`px-2 py-1 rounded text-xs font-medium focus:outline-none focus:ring-2 focus:ring-blue-400 ${theme === 'dark' ? 'bg-blue-600 text-white' : 'bg-gray-100 dark:bg-gray-700 dark:text-gray-200'}`}
            onClick={() => setTheme('dark')}
            aria-pressed={theme === 'dark'}
          >Dark</button>
          <button
            className={`px-2 py-1 rounded text-xs font-medium focus:outline-none focus:ring-2 focus:ring-blue-400 ${theme === 'system' ? 'bg-blue-600 text-white' : 'bg-gray-100 dark:bg-gray-700 dark:text-gray-200'}`}
            onClick={() => setTheme('system')}
            aria-pressed={theme === 'system'}
          >System</button>
        </div>
      </div>
    </div>
  );
};

export default PrintOptions;
