import React, { useState } from 'react';

const paperSizes = [
  { label: 'Letter (8.5x11.0 in)', value: 'letter' },
  { label: 'A4 (210x297 mm)', value: 'a4' },
];
const scales = [
  { label: '100%', value: '100' },
  { label: '95%', value: '95' },
  { label: '90%', value: '90' },
];

const ToggleSwitch = ({ checked, onChange, label, id }) => (
  <label className="flex items-center space-x-3 cursor-pointer select-none">
    <span className="relative inline-flex items-center">
      <input
        type="checkbox"
        checked={checked}
        onChange={e => onChange(e.target.checked)}
        id={id}
        className="sr-only peer"
      />
      <div className="w-11 h-6 bg-gray-300 peer-checked:bg-blue-600 rounded-full relative transition-colors duration-200">
        <div className={`absolute top-0.5 left-0.5 w-5 h-5 bg-white rounded-full shadow transform transition-transform duration-200 ${checked ? 'translate-x-5' : ''}`}></div>
      </div>
    </span>
    <span className="text-base text-gray-800 font-medium">{label}</span>
  </label>
);

const PrintOptions = ({ onPrint, printing }) => {
  const [cropMarks, setCropMarks] = useState(true);
  const [cutLines, setCutLines] = useState(false);
  const [blackCorners, setBlackCorners] = useState(true);
  const [skipBasicLands, setSkipBasicLands] = useState(false);
  const [printChecklist, setPrintChecklist] = useState(false);
  const [playtestWatermark, setPlaytestWatermark] = useState(false);
  const [paperSize, setPaperSize] = useState('letter');
  const [scale, setScale] = useState('100');

  return (
    <div className="bg-white rounded-xl shadow p-6 w-full flex flex-wrap items-center justify-center">
      <div className="flex flex-row flex-wrap items-center w-full space-x-0 md:space-x-6 space-y-4 md:space-y-0">
        {/* Print button */}
        <button
          className="flex items-center rounded-full px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white font-bold text-lg shadow transition focus:outline-none focus:ring-2 focus:ring-blue-400 disabled:opacity-60 mr-8"
          onClick={onPrint}
          disabled={printing}
          style={{ minWidth: 120 }}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="mr-3 h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 9V2h12v7M6 18H4a2 2 0 01-2-2v-5a2 2 0 012-2h16a2 2 0 012 2v5a2 2 0 01-2 2h-2m-6 0v4m0 0h4m-4 0H8" /></svg>
          Print
        </button>
        {/* Toggles group 1 */}
        <div className="flex flex-row flex-wrap items-center space-x-6 mr-8">
          <ToggleSwitch checked={cropMarks} onChange={setCropMarks} label="Crop marks" id="crop-marks" />
          <ToggleSwitch checked={blackCorners} onChange={setBlackCorners} label="Black corners" id="black-corners" />
        </div>
        {/* Toggles group 2 */}
        <div className="flex flex-row flex-wrap items-center space-x-6 mr-8">
          <ToggleSwitch checked={cutLines} onChange={setCutLines} label="Cut lines" id="cut-lines" />
          <ToggleSwitch checked={printChecklist} onChange={setPrintChecklist} label="Print checklist" id="print-checklist" />
        </div>
        {/* Toggles group 3 */}
        <div className="flex flex-row flex-wrap items-center space-x-6 mr-8">
          <ToggleSwitch checked={skipBasicLands} onChange={setSkipBasicLands} label="Skip basic lands" id="skip-basic-lands" />
          <ToggleSwitch checked={playtestWatermark} onChange={setPlaytestWatermark} label="Playtest watermark" id="playtest-watermark" />
        </div>
        {/* Dropdowns */}
        <div className="flex flex-row flex-wrap items-center space-x-4 ml-2">
          <label className="text-base text-gray-800 font-medium mr-2" htmlFor="paper-size">Paper Size</label>
          <select
            id="paper-size"
            className="px-3 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 text-base min-w-[170px]"
            value={paperSize}
            onChange={e => setPaperSize(e.target.value)}
          >
            {paperSizes.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
          <label className="text-base text-gray-800 font-medium ml-4 mr-2" htmlFor="scale">Scale</label>
          <select
            id="scale"
            className="px-3 py-2 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-400 text-base min-w-[90px]"
            value={scale}
            onChange={e => setScale(e.target.value)}
          >
            {scales.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
};

export default PrintOptions;
