import React, { useState } from 'react';
import { useTheme } from '../ThemeContext';

const paperSizes = [
  { label: 'Letter (8.5x11.0 in)', value: 'letter' },
  { label: 'A4 (210x297 mm)', value: 'a4' },
];
const scales = [
  { label: '100%', value: '100' },
  { label: '95%', value: '95' },
  { label: '90%', value: '90' },
];

const PrintOptions = ({ onPrint, printing }) => {
  const [cropMarks, setCropMarks] = useState(true);
  const [cutLines, setCutLines] = useState(false);
  const [blackCorners, setBlackCorners] = useState(true);
  const [skipBasicLands, setSkipBasicLands] = useState(false);
  const [printChecklist, setPrintChecklist] = useState(false);
  const [playtestWatermark, setPlaytestWatermark] = useState(false);
  const [paperSize, setPaperSize] = useState('letter');
  const [scale, setScale] = useState('100');
  const { theme, setTheme } = useTheme();

  return (
    <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg p-6 flex flex-col gap-4 w-full max-w-3xl">
      <div className="flex items-center justify-between mb-2">
        <div />
        <div className="flex gap-2 items-center">
          <span className="text-xs text-gray-500 dark:text-gray-300">Theme:</span>
          <button
            className={`px-2 py-1 rounded text-xs font-medium focus:outline-none focus:ring-2 focus:ring-blue-400 ${theme === 'light' ? 'bg-blue-600 text-white' : 'bg-gray-100 dark:bg-gray-700 dark:text-gray-200'}`}
            onClick={() => setTheme('light')}
            aria-pressed={theme === 'light'}
          >Light</button>
          <button
            className={`px-2 py-1 rounded text-xs font-medium focus:outline-none focus:ring-2 focus:ring-blue-400 ${theme === 'dark' ? 'bg-blue-600 text-white' : 'bg-gray-100 dark:bg-gray-700 dark:text-gray-200'}`}
            onClick={() => setTheme('dark')}
            aria-pressed={theme === 'dark'}
          >Dark</button>
          <button
            className={`px-2 py-1 rounded text-xs font-medium focus:outline-none focus:ring-2 focus:ring-blue-400 ${theme === 'system' ? 'bg-blue-600 text-white' : 'bg-gray-100 dark:bg-gray-700 dark:text-gray-200'}`}
            onClick={() => setTheme('system')}
            aria-pressed={theme === 'system'}
          >System</button>
        </div>
      </div>
      <div className="flex items-center gap-4 mb-2">
        <button
          className="flex items-center gap-2 bg-blue-600 hover:bg-blue-700 text-white font-semibold px-4 py-2 rounded shadow disabled:opacity-60"
          onClick={onPrint}
          disabled={printing}
        >
          <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 9V2h12v7M6 18H4a2 2 0 01-2-2v-5a2 2 0 012-2h16a2 2 0 012 2v5a2 2 0 01-2 2h-2m-6 0v4m0 0h4m-4 0H8" /></svg>
          Print
        </button>
        <div className="flex flex-col gap-1">
          <label className="flex items-center gap-2 cursor-pointer">
            <input type="checkbox" checked={cropMarks} onChange={() => setCropMarks(v => !v)} className="form-checkbox" />
            <span>Crop marks</span>
          </label>
          <label className="flex items-center gap-2 cursor-pointer">
            <input type="checkbox" checked={blackCorners} onChange={() => setBlackCorners(v => !v)} className="form-checkbox" />
            <span>Black corners</span>
          </label>
        </div>
        <div className="flex flex-col gap-1">
          <label className="flex items-center gap-2 cursor-pointer">
            <input type="checkbox" checked={cutLines} onChange={() => setCutLines(v => !v)} className="form-checkbox" />
            <span>Cut lines</span>
          </label>
          <label className="flex items-center gap-2 cursor-pointer">
            <input type="checkbox" checked={printChecklist} onChange={() => setPrintChecklist(v => !v)} className="form-checkbox" />
            <span>Print checklist</span>
          </label>
        </div>
        <div className="flex flex-col gap-1">
          <label className="flex items-center gap-2 cursor-pointer">
            <input type="checkbox" checked={skipBasicLands} onChange={() => setSkipBasicLands(v => !v)} className="form-checkbox" />
            <span>Skip basic lands</span>
          </label>
          <label className="flex items-center gap-2 cursor-pointer">
            <input type="checkbox" checked={playtestWatermark} onChange={() => setPlaytestWatermark(v => !v)} className="form-checkbox" />
            <span>Playtest watermark</span>
          </label>
        </div>
      </div>
      <div className="flex items-center gap-4">
        <div className="flex flex-col">
          <label className="text-xs text-gray-600 mb-1">Paper Size</label>
          <select
            className="border rounded px-2 py-1 text-sm"
            value={paperSize}
            onChange={e => setPaperSize(e.target.value)}
          >
            {paperSizes.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
        <div className="flex flex-col">
          <label className="text-xs text-gray-600 mb-1">Scale</label>
          <select
            className="border rounded px-2 py-1 text-sm"
            value={scale}
            onChange={e => setScale(e.target.value)}
          >
            {scales.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
      </div>
    </div>
  );
};

export default PrintOptions;
